<script>
let data = {
  started: false,
  timePassed: 0,
  options: { year: 'numeric', month: 'long', day: 'numeric', hour: 'numeric', minute: 'numeric' },
  sound: {
    soundTouch: new Audio('sound/touch.mp3'),
    soundStart: new Audio('sound/start.mp3'),
    soundEnd: new Audio('sound/end.mp3')
  },
  stat: {
    timeStart: 0,
    timeTotal: 0,
    el1: 0,
    el2: 0,
    el3: 0,
    el4: 0,
    el5: 0,
    el6: 0,
    el7: 0,
    el8: 0,
    oneHeight: ''
  },
  statHtml: '',
  statShow: false,
}

export default {
  name: 'Octagon',
  data: function () {
    return data;
  },
  mounted: function () {
    this.sound.soundTouch.load();
    this.sound.soundStart.load();
    this.sound.soundEnd.load();
  },
  computed: {
    soundFx: function() {
      return this.$parent.soundFx;
    }
  },
  methods: {
    startStop: function () {
      
      if (!data.started) {
        if (this.soundFx) data.sound.soundStart.cloneNode(true).play();
        data.stat.timeStart = new Date().getTime();
        let touchAreas = document.getElementsByClassName('touch-area');
        for (var i = 0; i < touchAreas.length; i++) {
          touchAreas[i].setAttribute("style", "cursor:pointer;");
        }
        if (this.$parent.timer > 0) {
          // convert timer to miliseconds
          let timer = this.$parent.timer * 60 * 1000;
          const self = this;
          self.timePassed = 0;
          function timeCalc () {
            self.timePassed += 1000;
            if (self.timePassed > timer) {
              clearInterval(timerVar);
              self.autoEnd();
            }
          }
          window.timerVar = setInterval(timeCalc, 1000);
        }
      } 
      else {
        document.body.addEventListener('click', e => { this.closeStat() });

        clearInterval(window.timerVar);
        let touchAreas = document.getElementsByClassName('touch-area');
        for (var i = 0; i < touchAreas.length; i++) {
          touchAreas[i].setAttribute("style", "cursor:default;");
        }
        if (this.soundFx) data.sound.soundEnd.cloneNode(true).play();

        // Show stats
        data.stat.timeTotal = new Date().getTime() - data.stat.timeStart;
        let seconds = data.stat.timeTotal / 1000;
        let minutes = Math.floor(seconds / 60);
        seconds = seconds - minutes * 60;
        data.stat.timeMeditatedHtml = '<span style="font-size:19px;">'+ minutes + ' min. ' + Math.round(seconds) + ' sec.</span><br><span style="font-size:13px">Time meditated<br><br>' + new Date().toLocaleDateString("en-US", this.options) + '</span>';

        let maxEl = Math.max(data.stat.el1, data.stat.el2, data.stat.el3, data.stat.el4, data.stat.el5, data.stat.el6, data.stat.el7, data.stat.el8);

        if(maxEl != 0) {
          data.stat.oneHeight = 110 / maxEl;

          function dataShowDelayed() {
            data.statShow = !data.statShow;
          }
          setTimeout(dataShowDelayed, 1300);
        }

      }

      data.started = !data.started
      var center = document.getElementById('center')
      var menu = document.getElementById('menu')
      var elements = document.getElementsByClassName('elements')

      var arr = Array.from(elements)
      if (!data.started) {
        arr = arr.reverse()
      }

      arr.forEach(function (el, index) {
        setTimeout(function () {
          el.classList.toggle('elements-active')
        },
        130 * index)
      })
      
      this.$parent.firstVisit = false;
      
      center.classList.toggle('center-started')
      menu.classList.toggle('menu-started')

    },

    svgY: function(el) {
      return Math.round(130 - (data.stat[el] * data.stat.oneHeight));
    },

    svgHeight: function(el) {
      return Math.round(data.stat[el] * data.stat.oneHeight)
    },

    autoEnd: function () {
      document.body.addEventListener('click', e => { this.closeStat() });
      
      let touchAreas = document.getElementsByClassName('touch-area');
      for (var i = 0; i < touchAreas.length; i++) {
        touchAreas[i].setAttribute("style", "cursor:default;");
      }
      if (this.soundFx) data.sound.soundEnd.cloneNode(true).play();


      // Show stats
      data.stat.timeTotal = new Date().getTime() - data.stat.timeStart;
      let seconds = data.stat.timeTotal / 1000;
      let minutes = Math.floor(seconds / 60);
      seconds = seconds - minutes * 60;
      data.stat.timeMeditatedHtml = '<span style="font-size:19px;">'+ minutes + ' min. ' + Math.round(seconds) + ' sec.</span><br><span style="font-size:13px">Time meditated<br><br>' + new Date().toLocaleDateString("en-US", this.options) + '</span>';

      let maxEl = Math.max(data.stat.el1, data.stat.el2, data.stat.el3, data.stat.el4, data.stat.el5, data.stat.el6, data.stat.el7, data.stat.el8);

      if(maxEl != 0) {
        data.stat.oneHeight = 110 / maxEl;

        function dataShowDelayed() {
          data.statShow = !data.statShow;
        }
        setTimeout(dataShowDelayed, 1300);
      }

      data.started = !data.started
      var center = document.getElementById('center')
      var menu = document.getElementById('menu')
      var elements = document.getElementsByClassName('elements')

      var arr = Array.from(elements)
      if (!data.started) {
        arr = arr.reverse()
      }

      arr.forEach(function (el, index) {
        setTimeout(function () {
          el.classList.toggle('elements-active')
        },
        130 * index)
      })
      
      this.$parent.firstVisit = false;
      
      center.classList.toggle('center-started');
      menu.classList.toggle('menu-started');


    },

    onTap: function (el) {
      function toggle () {
        element.classList.toggle('elements-touch')
      }
      if (data.started) {
        var element = document.getElementsByClassName('element-' + el)[0];
        var elX = 'el' + el;
        data.stat[elX]++;
        if (this.soundFx) data.sound.soundTouch.cloneNode(true).play();
        toggle();
        setTimeout(toggle, 200);
      }
    },

    closeStat: function () {
      if (data.statShow) {
        data.statShow = !data.statShow;

        data.stat = {
          timeStart: 0,
          timeTotal: 0,
          el1: 0,
          el2: 0,
          el3: 0,
          el4: 0,
          el5: 0,
          el6: 0,
          el7: 0,
          el8: 0
        }

        document.body.removeEventListener('click', e => { this.closeStat() });
      }
    }
  }
}
</script>


